using System;using System.Collections.Generic;using System.ComponentModel;using System.Data;using System.Drawing;using System.Linq;using System.Text;using System.Threading.Tasks;using System.Windows.Forms;using System.Media;namespace AyakkabıSatısOtomasyonu{    public partial class AçılışEkranı : Form    {        public AçılışEkranı()        {            InitializeComponent();        }        private void AçılışEkranı_Load(object sender, EventArgs e)        {            SoundPlayer ses = new SoundPlayer();            string dizin = Application.StartupPath + @"\\ses.wav";            ses.SoundLocation = dizin;            ses.Play();        }        private void timer1_Tick(object sender, EventArgs e)        {            SoundPlayer ses = new SoundPlayer();            string dizin = Application.StartupPath + @"\\ses.wav";            ses.SoundLocation = dizin;                        panel1.Width += 5;            if (panel1.Width >= 360)            {                timer1.Stop();                NikeGiris nk = new NikeGiris();                nk.Show();                this.Hide();                ses.Stop();                            }                    }    }}